<?php class AllusersVue extends VueAll{public function allusers($data){ $this->content .="<table style='width:50%; margin-top:20px' align='center' class='table table-striped'><tr><th>id</th><th>nom</th><th>email</th><th>tel</th></tr>"; foreach ($data as $d) { $this->content .= "<tr><td>".$d["id"]."</td><td>".$d["nom"]."</td><td>".$d["email"]."</td><td>".$d["tel"]."</td></tr>"; } "</table>";}}